--create a table with distinct nairobi uid's
create table public.nairobidistinctuid as
select distinct osm_uid
from (
    select osm_uid from public.nairobihot_line
    union 
	select osm_uid from public.nairobihot_point
	union
	select osm_uid from public.nairobihot_polygon	
) as userid


