--Create new point table with bulk imports removed
/*  Original table = 1544525 records
	bulk removed table = 893721 records	*/
create table public.nairobi_point_no_bulk as
select * from public.nairobihot_point
where (osm_timestamp::date)::text || osm_uid::text
not in (select (osm_timestamp::date)::text || osm_uid::text
from public.nairobihot_point
group by (osm_timestamp::date)::text || osm_uid::text
having count(*) > 1000
)

--Create new line table with bulk imports removed
/*  Original table = 125941 records
	bulk removed table = 125941 records	*/
create table public.nairobi_line_no_bulk as
select * from public.nairobihot_line
where (osm_timestamp::date)::text || osm_uid::text
not in (select (osm_timestamp::date)::text || osm_uid::text
from public.nairobihot_line
group by (osm_timestamp::date)::text || osm_uid::text
having count(*) > 1000
)

